package summerlee.wedding.controller;import java.util.concurrent.TimeUnit;import javax.servlet.http.HttpServletRequest;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import summerlee.wedding.websocket.Broadcaster;/** * 基础控制器 *  * @file BaseController * @author libin * @Create 2016-12-26 */@Controllerpublic class ShowController {		@RequestMapping(value="/")	public String index(Model model, HttpServletRequest request) {		model.addAttribute("host", request.getHeader("Host"));		model.addAttribute("port", request.getLocalPort());		return "index";	}		@RequestMapping(value="/pop")	public @ResponseBody String danmu(String danmu) {		new Thread(){			@Override			public void run() {				while(true){					Broadcaster.addDanmu("我们结婚啦~");					try {						Thread.sleep(TimeUnit.SECONDS.toMillis(1));					} catch (Exception e) {											}				}			}		}.start();		if(StringUtils.isNotBlank(danmu)){			Broadcaster.addDanmu(danmu);		}		return "success";	}}