package summerlee.wedding.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import summerlee.wedding.websocket.Broadcaster;/** * 基础控制器 *  * @file BaseController * @author libin * @Create 2016-12-26 */@Controllerpublic class ShowController {		@RequestMapping(value="/")	public String index(Model model) {		return "index";	}		@RequestMapping(value="/add")	public @ResponseBody String danmu(String danmu) {		Broadcaster.addDanmu(danmu);		return danmu;	}}