SELECT cm.sn,cm.latitude,cm.longitude,cm.state,c.plateNumber FROM
car_msg_info AS cm JOIN car_info AS c ON cm.sn=c.sn WHERE cm.sn= #{sn}
ORDER BY cm.addTime desc LIMIT 0,1

